import axios from "axios";

export default {
data: () => ({
    overlay:true,
    valid: true,
    information: {},
    valid: true,
    roles:{},
    rules:{
        nameRules: [
            v => !!v || 'Name is required',
            v => (v && v.length >= 3) || 'Name must be more than 3 characters',
        ],
        emailRules: [
            v => !!v || 'E-mail is required',
            v => /.+@.+\..+/.test(v) || 'E-mail must be valid',
        ],
        passwordRules:[
            v => !!v || "Senha é obrigatória.",
            v => (v && v.length >= 8) || "A senha deve ser maior que 8 caracteres.",
            v => (v && v.length <= 16) || "A senha deve ser menor que 16 caracteres."
        ],
        radioRules:[
            v => !!v || "Escolha sua alocação",
        ]

    },
}),
mounted(){
    axios.get("http://127.0.0.1:80/servidorPHP/index.php/buscarTiposUsuarios")
    .then(res =>{
        this.roles = res.data
    })
    .catch(e =>{
        console.log(e)
    })
},
methods: {
    async validateName() {
        let url = `http://127.0.0.1:80/servidorPHP/index.php/verificarNome?name=${this.information.userName}`
        let result = await axios.get(url)
        this.valid = result.data.code == 1

    },
    submit(){
        if(this.validate()){
            axios
            .post("http://127.0.0.1:80/servidorPHP/index.php/cadastrar", this.information)
            .then(res => {
                console.log(res);
                return true;
            })
            .catch(e => {
                this.errorShow = true
                console.log(e)
                return false;
            })
        }
    },
    validate () {
        if (this.$refs.form.validate()) {
            return true
        }
    },
    reset () {
        this.$refs.form.reset()
    },
    resetValidation () {
        this.$refs.form.resetValidation()
    },
},
}
